import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react'

function App() {
  const [ sointu, setSointu] = useState('C');

  let laskuri = 3
  let vastaus = []

  const csointu = "C";
  const collection = new Map();
  collection.set("C", ["c", "e", "g"]);
  collection.set("C#", ["c", "e", "g"]);
  collection.set("D", ["c", "e", "g"]);
  collection.set("D#", ["c", "e", "g"]);
  collection.set("E", ["c", "e", "g"]);
  collection.set("F", ["c", "e", "g"]);
  collection.set("F#", ["c", "e", "g"]);
  collection.set("G", ["c", "e", "g"]);
  collection.set("G#", ["c", "e", "g"]);
  collection.set("A", ["c", "e", "g"]);
  collection.set("A#", ["c", "e", "g"]);
  collection.set("H/B", ["c", "e", "g"]);

  console.log(collection.get(csointu)); 

  const tallennaSavel = (e) => {
    vastaus.push(e.target.value)
    laskuri -= 1
    if (laskuri == 0) {
      console.log(vastaus)
      if (oikeatSavelet(collection.get(sointu), vastaus)) {
        console.log("oikein!")
        }
      else {
        console.log("v채채rin :(")
        }
      vastaus = []
      laskuri = 3
      }
    }  

  function oikeatSavelet(array1, array2) {
    if (array1.length === array2.length) {
      return array1.every(element => {
        if (array2.includes(element)) {
          return true;
        }

        return false;
      });
    }
    return false;
  }


  return (
    <div className="App">
      <header className="App-header">
        <h1>{sointu}</h1> 
        <p>
        <button type="button" onClick={tallennaSavel} value="c">c</button><br/>
        <button type="button" onClick={tallennaSavel} value="e">e</button><br/>
        <button type="button" onClick={tallennaSavel} value="g">g</button><br/>
        </p>
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
